{
  "name": "example-ignite-ui-web-components",
  "description": "This project provides example of using Ignite UI for Web Components",
  "author": "Infragistics",
  "version": "1.0.0",
  "license": "",
  "private": true,
  "homepage": ".",
  "main": "src/index.ts",
  "scripts": {
    "build": "npm run build:prod",
    "build:dev": "webpack --mode development --config ./webpack.config.js --progress --color --display-error-details",
    "build:prod": "webpack --env.NODE_ENV=production --mode production --config ./webpack.config.js  --progress  --color --display-error-details --bail",
    "serve:dev": "node --max-old-space-size=8192 node_modules/webpack-dev-server/bin/webpack-dev-server.js --env.legacy=false --mode development --config ./webpack.config.js --hot --inline --progress --watch --open",
    "serve:prod": "webpack-dev-server --env.NODE_ENV=production  --mode production --config ./webpack.config.js --port 3000 --host 0.0.0.0 --hot --inline --progress  --watch --open --content-base dist/",
    "start": "npm run serve:dev",
    "build:legacy": "npm run build:prod:legacy",
    "build:dev:legacy": "webpack --env.legacy=true  --mode development --config ./webpack.config.js --progress --color --display-error-details",
    "build:prod:legacy": "webpack --env.NODE_ENV=production --env.legacy=true  --mode production --config ./webpack.config.js  --progress  --color --display-error-details --bail",
    "serve:dev:legacy": "node --max-old-space-size=8192 node_modules/webpack-dev-server/bin/webpack-dev-server.js --env.legacy=true --mode development --config ./webpack.config.js --hot --inline --progress --watch --open",
    "serve:prod:legacy": "webpack-dev-server --env.NODE_ENV=production --env.legacy=true --mode production --config ./webpack.config.js --port 3000 --host 0.0.0.0 --hot --inline --progress  --watch --open --content-base dist/",
    "start:legacy": "npm run serve:dev:legacy"
  },
  "dependencies": {
    "igniteui-webcomponents-core": "3.2.2",
//ifdef charts, maps
    "igniteui-webcomponents-charts": "3.2.2",
//endifdef charts, maps
//ifdef grid
    "igniteui-webcomponents-datasources": "3.2.2",
//endifdef grid
//ifdef excel, spreadsheet
    "igniteui-webcomponents-excel": "3.2.2",
//endifdef excel, spreadsheet
//ifdef gauges
    "igniteui-webcomponents-gauges": "3.2.2",
//endifdef gauges
//ifdef grid
    "igniteui-webcomponents-grids": "3.2.2",
//endifdef grid
//ifdef grid, inputs
    "igniteui-webcomponents-inputs": "3.2.2",
//endifdef grid, inputs
//ifdef grid, editor, layouts
    "igniteui-webcomponents-layouts": "3.2.2",
//endifdef grid, editor, layouts
//ifdef maps
    "igniteui-webcomponents-maps": "3.2.2",
//endifdef maps
//ifdef spreadsheet
    "igniteui-webcomponents-spreadsheet": "3.2.2",
    "igniteui-webcomponents-spreadsheet-chart-adapter": "3.2.2",
//endifdef spreadsheet
    "@webcomponents/custom-elements": "^1.4.1",
    "@webcomponents/template": "^1.4.2",
    "babel-runtime": "^6.26.0",
    "core-js": "^3.6.5",
//ifdef editor
    "igniteui-webcomponents": "^3.2.0",
//endifdef editor
    "lit-html": "^2.2.0",
    "tslib": "^2.0.0"
  },
  "devDependencies": {
    "@babel/cli": "^7.8.3",
    "@babel/core": "^7.8.3",
    "@babel/plugin-proposal-class-properties": "^7.8.3",
    "@babel/plugin-transform-runtime": "^7.10.0",
    "@babel/preset-env": "^7.8.3",
    "@babel/preset-typescript": "^7.8.3",
    "@types/source-map": "^0.5.7",
    "babel-loader": "^8.1.0",
    "babel-plugin-transform-custom-element-classes": "^0.1.0",
    "fork-ts-checker-webpack-plugin": "^4.1.5",
    "html-webpack-plugin": "^4.3.0",
    "source-map": "^0.7.3",
    "typescript": "^4.4.4",
    "webpack": "^4.43.0",
    "webpack-cli": "^3.3.11",
    "webpack-dev-server": "^3.11.0",
    "parcel-bundler": "^1.6.1",
    "css-loader": "^1.0.0",
    "csv-loader": "^3.0.2",
    "file-loader": "^4.2.0",
    "style-loader": "^0.22.1",
    "xml-loader": "^1.2.1",
    "worker-loader": "^3.0.8"
    }
}

const ForkTsCheckerWebpackPlugin = require('fork-ts-checker-webpack-plugin');
const HtmlWebpackPlugin = require('html-webpack-plugin');
const path = require('path');
const webpack = require('webpack');

module.exports = env => {
  console.log("env:");
  console.log(env);
  const isLegacy = !!env.legacy && !(env.legacy == "false");
  const isProd = env.NODE_ENV === 'production';
  return {
    entry: isLegacy ? [
      path.resolve(__dirname, 'node_modules/@webcomponents/custom-elements'),
      path.resolve(__dirname, 'node_modules/@webcomponents/template'),
      path.resolve(__dirname, 'src')
    ] : path.resolve(__dirname, 'src'),
    devtool: isProd ? false : 'source-map',
    output: {
      filename: isProd ? '[chunkhash].bundle.js' : '[hash].bundle.js',
      path: path.resolve(__dirname, 'dist'),
    },

    resolve: {
      mainFields: ['fesm2015', 'module', 'main'],
      extensions: ['.ts', '.js', '.json']
    },

    module: {
      rules: [
        { test: /\.(png|svg|jpg|gif)$/, use: ['file-loader'] },
        { test: /\.(csv|tsv)$/, use: ['csv-loader'] },
        { test: /\.xml$/, use: ['xml-loader'] },
        { test: /\.css$/, loaders: ['style-loader', 'css-loader'] },
        { test: /\.(ts|js)$/, loader: 'babel-loader',
        options: {
          "compact": isProd ? true : false,
          "presets": [
            ["@babel/preset-env", {
              "useBuiltIns": "usage",
              "corejs": 3,
              "targets": {
                "browsers": isLegacy ? ["defaults"] : [
                  "last 2 Chrome versions",
                  "last 2 Safari versions",
                  "last 2 iOS versions",
                  "last 2 Firefox versions",
                  "last 2 Edge versions"]
              }
            }],
            "@babel/preset-typescript"
          ],
          "plugins": [
            "@babel/plugin-proposal-class-properties",
            "@babel/plugin-transform-runtime"
          ]
        },
        exclude:
        function(modulePath) {
          return /node_modules/.test(modulePath) &&
            !/igniteui-webcomponents/.test(modulePath) &&
            !/lit-html/.test(modulePath);
        }
    }],
    },

    plugins: [
      new webpack.DefinePlugin({
        'process.env': {
          NODE_ENV: env.NODE_ENV
        }
      }),
      new HtmlWebpackPlugin({
        title: 'for-cs',
        template: 'index.html'
      }),
      new ForkTsCheckerWebpackPlugin()
    ]
  };
};

